import {useState} from 'react';

import Layout from "../components/Layout";

import axios from "axios";








const Register =()=>{
       const [state,setState] = useState({
           name:'',
           email:'',
           password:'',
           error:'',
           sucess:'',
           buttonText:'Register'
       });


       const {name,email,password,error,sucess,buttonText} = state;

     const handleChange = (name) => (e)=>{
              setState({...state,
                [name]:e.target.value,
                error:'',
                sucess:'',
                buttonText:'Register'
            })
     }


     const handleSubmit = (e)=>{
           e.preventDefault();
            axios.post('https://localhost:8000/api/register',{
              name,email,password
            })
            .then(response=> console.log(response))
            .catch(error=> console.log(error))
     }


     const registerForm = ()=>(
        <form onSubmit={handleSubmit}> 
            <div className="form-group">
              <input value={name} onChange={handleChange('name')} type="text" className="form-control" placeholder="Type your name"/>
            </div>

             <div className="form-group">
              <input value={email} onChange={handleChange('email')} type="email" className="form-control" placeholder="Type your email"/>
            </div>

             <div className="form-group">
              <input value={password} onChange={handleChange('password')} type="password" className="form-control" placeholder="Type your password"/>
            </div>

            <div className="form-group">
              <button className="btn btn-outline-warning">{buttonText}</button>
            </div>
            
        </form>
     )
 



return <Layout>
    <div className="col-md-6 offset-md-3">
     <h1>Register</h1>
     <br/>
     {registerForm()}
     <br/>
     {JSON.stringify(state)}
    </div>
    
    </Layout>
}
export default Register;
